      <section id="home" className="h-screen flex flex-col justify-center items-center text-center px-6 bg-black/60">
        <motion.h1
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-5xl font-bold text-white"
        >
          Hey, Iâ€™m Isaiah! ðŸ‘‹
        </motion.h1>
        <motion.p
          initial={{ opacity: 0, y: 20 }}
