import React, { useState } from "react";
import { motion } from "framer-motion";

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId).scrollIntoView({ behavior: "smooth" });
  };

  return (
    <motion.nav
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.5, duration: 0.5, ease: "easeOut" }}
      className="navbar text-gray-900 p-4 flex justify-between items-center"
    >
      {/* Logo */}
      <a className="btn btn-ghost text-xl font-bold">Dev Isaiah</a>

      {/* Hamburger Menu (Mobile) */}
      <button 
        className="z-[1] mt-3 w-52 p-2 lg:hidden text-gray-800 focus:outline-none" 
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle Menu"
      >
        â˜°
      </button>

      {/* Navigation Links (Animated) */}
      <motion.div
        initial={{ opacity: 0, y: -10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5, duration: 0.5 }}
        className={`lg:flex flex-1 justify-center ${isOpen ? "block" : "hidden"} lg:block`}
      >
        <ul className="menu menu-horizontal bg-base rounded-box flex space-x-6 text-gray-800 font-semibold p-2">
          {["Home", "About Me", "Skills", "Portfolio"].map((item, index) => (
            <motion.li
              key={item}
              whileHover={{ scale: 1.1 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <a href="#" title={`Go to ${item}`}>{item}</a>
            </motion.li>
          ))}
        </ul>
      </motion.div>

      {/* Profile Dropdown */}
      <div className="dropdown dropdown-end">
        <motion.div 
          whileTap={{ scale: 0.9 }} 
          className="btn btn-ghost btn-circle avatar"
        >
          <div className="w-10 rounded-full">
            <img 
              alt="User profile" 
              src="/Isaiah.jpg" 
              onError={(e) => e.target.src = "/default-avatar.png"} 
            />
          </div>
        </motion.div>
        <ul className="menu menu-sm dropdown-content bg-base-100 rounded-box mt-3 w-52 p-2 shadow">
        <li><button onClick={() => scrollToSection("home")}>Home</button></li>
        <li><button onClick={() => scrollToSection("about")}>About Me</button></li>
        <li><button onClick={() => scrollToSection("skills")}>Skills</button></li>
        <li><button onClick={() => scrollToSection("portfolio")}>Portfolio</button></li>
        </ul>
      </div>
    </motion.nav>
  );
}

export default Navbar;
