import { motion, useMotionValue, useSpring } from "framer-motion";
import { useEffect } from "react";

const AnimatedBackground = () => {
  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);
  const smoothX = useSpring(mouseX, { stiffness: 30, damping: 20 });
  const smoothY = useSpring(mouseY, { stiffness: 30, damping: 20 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      mouseX.set(e.clientX - window.innerWidth / 2);
      mouseY.set(e.clientY - window.innerHeight / 2);
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, [mouseX, mouseY]);

  return (
    <motion.div className="absolute inset-0 -z-10">
      <motion.div
        className="absolute w-96 h-96 bg-blue-400 rounded-full opacity-50 blur-3xl"
        style={{ x: smoothX, y: smoothY }}
      />
      <motion.div
        className="absolute w-80 h-80 bg-purple-400 rounded-full opacity-50 blur-3xl"
        style={{ x: smoothX, y: smoothY }}
      />
    </motion.div>
  );
};

export default AnimatedBackground;
