import React from "react";


function About() {
  return (
    <section
      id="about"
      className="relative min-h-screen flex flex-col justify-center items-center text-center px-6 overflow-hidden bg-gradient-to-br from-gray-900 to-gray-800"
    >
      {/* Particle Effect */}
      <AnimatedBackground
        particleCount={75}
        particleColor="rgba(255, 255, 255, 0.3)"
        particleSize={2}
        speed={0.5}
      />

      {/* Content */}
      <div className="relative z-10 space-y-6">
        <h2 className="text-4xl md:text-5xl font-bold text-white bg-clip-text text-transparent bg-gradient-to-r from-blue-400 to-purple-500">
          About Me
        </h2>
        <p className="text-lg md:text-xl text-gray-300 max-w-2xl leading-relaxed">
          With expertise in frontend development, UI/UX design, and backend engineering, I specialize in creating{" "}
          <span className="font-semibold text-white">high-performance web applications</span> that deliver seamless user
          experiences. Passionate about solving complex problems and building scalable, modern solutions.
        </p>
        <button
          className="mt-6 px-6 py-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white font-semibold rounded-lg hover:from-blue-600 hover:to-purple-700 transition-all duration-300 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-offset-2"
          onClick={() => {
            // Smooth scroll to the contact section or another section
            document.getElementById("contact")?.scrollIntoView({ behavior: "smooth" });
          }}
        >
          Get in Touch
        </button>
      </div>
    </section>
  );
}

export default About;
