import React from "react";

function Skills() {
  const languages = [
    { name: "HTML", logo: "/logos/html.svg" },
    { name: "CSS", logo: "/logos/css.svg" },
    { name: "JavaScript", logo: "/logos/javascript.svg" },
    { name: "Python", logo: "/logos/python.svg" },
    { name: "C", logo: "/logos/c.svg" },
  ];

  const frameworks = [
    { name: "Bootstrap", logo: "/logos/boostrap.svg" },
    { name: "Tailwind CSS", logo: "/logos/tailwind.svg" },
    { name: "Django", logo: "/logos/django.svg" },
    { name: "Flask", logo: "/logos/flask.svg" },
    { name: "ReactJS", logo: "/logos/react.svg" },
    { name: "Framer Motion", logo: "/logos/framer.svg" },
  ];

  const other = [
    { name: "MySQL", logo: "/logos/mysql.svg" },
    { name: "Npm", logo: "/logos/npm.svg" },
    { name: "Git", logo: "/logos/git.svg" },
    { name: "GitHub", logo: "/logos/github.svg" },
    { name: "Vercel", logo: "/logos/vercel.svg" },
    { name: "Postman", logo: "/logos/postman.svg" },
    { name: "Linux", logo: "/logos/linux.svg" },
  ];

  const renderSkills = (skills, title) => (
    <div className="mb-12">
      <h3 className="text-2xl font-bold text-white mb-6">{title}</h3>
      <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-5 lg:grid-cols-6 gap-4">
        {skills.map((skill) => (
          <div
            key={skill.name}
            className="group p-3 bg-white/5 backdrop-blur-md rounded-full shadow-lg hover:bg-white/10 transition-all duration-300 ease-in-out transform hover:scale-105 flex flex-col items-center justify-center space-y-1"
          >
            <img
              src={skill.logo}
              alt={skill.name}
              className="w-8 h-8 object-contain transition-transform duration-300 group-hover:scale-110"
            />
            <span className="text-white text-xs font-medium">{skill.name}</span>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <section id="skills" className="min-h-screen flex flex-col justify-center items-center text-center px-6 py-12 bg-gradient-to-br from-gray-900 to-gray-800">
      <h2 className="text-4xl font-bold text-white mb-12">Skills & Technologies</h2>
      {renderSkills(languages, "Languages")}
      {renderSkills(frameworks, "Frameworks & Libraries")}
      {renderSkills(other, "Other Tools & Technologies")}
    </section>
  );
}

export default Skills;
