import { motion } from "framer-motion";

const AnimatedBackground = () => {
  return (
    <motion.div
      className="fixed inset-0 -z-10 overflow-hidden"
      initial={{ background: "linear-gradient(45deg, #6EE7B7, #3B82F6)" }}
      animate={{
        background: [
          "linear-gradient(45deg, #6EE7B7, #3B82F6)",
          "linear-gradient(45deg, #3B82F6, #9333EA)",
          "linear-gradient(45deg, #9333EA, #6EE7B7)",
        ],
      }}
      transition={{
        duration: 10,
        repeat: Infinity,
        repeatType: "mirror",
        ease: "linear",
      }}
    />
  );
};

export default AnimatedBackground;
