import React, { useState } from 'react';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="navbar bg-base-100 text-gray-900 p-4 flex justify-between items-center">

      {/* Logo */}
      <a className="text-xl font-bold">Dev Isaiah</a>

      {/* Hamburger Menu (Mobile) */}
      <button 
        className="lg:hidden text-gray-800 focus:outline-none" 
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle Menu"
      >
        â˜°
      </button>

      {/* Navigation Links (Responsive) */}
      <div className={`lg:flex flex-1 justify-center ${isOpen ? "block" : "hidden"} lg:block`}>
        <ul className="menu menu-horizontal bg-base-200 rounded-box flex space-x-6 text-gray-800 font-semibold p-2">
          <li><a href="#" title="Go to Home">Home</a></li>
          <li><a href="#" title="Learn About Me">About Me</a></li>
          <li><a href="#" title="Check My Skills">Skills</a></li>
          <li><a href="#" title="View My Portfolio">Portfolio</a></li>
        </ul>
      </div>

      {/* Profile Dropdown */}
      <div className="dropdown dropdown-end">
        <div role="button" className="btn btn-ghost btn-circle avatar">
          <div className="w-10 rounded-full">
            <img 
              alt="User profile" 
              src="/Isaiah.jpg" 
              onError={(e) => e.target.src = "/default-avatar.png"} 
            />
          </div>
        </div>
        <ul className="menu menu-sm dropdown-content bg-base-100 rounded-box mt-3 w-52 p-2 shadow">
          <li><a href="#">Profile</a></li>
          <li><a href="#">Settings</a></li>
          <li><a href="#">Logout</a></li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
